#!/bin/sh
mkdir -p "${PROJECT_DIR}/_Frameworks"

# copy over MobileDevice.framework from its location
MD_FRAMEWORK_LOCATION=/System/Library/PrivateFrameworks/MobileDevice.framework
if [ -d "$MD_FRAMEWORK_LOCATION" ]; then
    rsync -r $MD_FRAMEWORK_LOCATION "${PROJECT_DIR}/_Frameworks" --exclude=XPCServices --links
fi

# check if this new location exists, if it does, copy it instead
NEW_MD_FRAMEWORK_LOCATION=/Library/Apple/System/Library/PrivateFrameworks/MobileDevice.framework
if [ -d "$NEW_MD_FRAMEWORK_LOCATION" ]; then
rsync -r $NEW_MD_FRAMEWORK_LOCATION "${PROJECT_DIR}/_Frameworks" --exclude=XPCServices --links
fi


